import React, { useState, useEffect } from 'react';
import { Switch, Route, Link, Redirect } from 'react-router-dom';
import axios from 'axios';

import Tab from './Tab';
import Card from './Card';

import sampleCardData from './sampleCardData.json';
import './Card.css';

const Category = () => {
  const [selectedTab, setSelectedTab] = useState('department');
  const [departmentData, setDepartmentData] = useState([]);
  const [designationData, setDesignationData] = useState([]);

  useEffect(() => {
    fetchDepartmentData();
    fetchDesignationData();
  }, []);

  const fetchData = async () => {
    try {
      // const response = await axios.get('https://testsyncoffice.netlify.app/.netlify/functions/api/getDepartmentData');
      // setData(response.data);
      setData(sampleCardData.designationData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  return (
    <div className="category">
      <Tab selectedTab={selectedTab} onTabChange={handleTabChange} />
      <div className="card-container">
        <Switch>
          <Route exact path="/category">
            <Redirect to="/category/department" />
          </Route>
          <Route path="/category/department">
            {data.map((item) => (
              <Card
                key={item.id}
                title={item.designation}
                description={item.description}
                date={item.date}
              />
            ))}
          </Route>
          <Route path="/category/designation">
            {data.map((item) => (
              <Card
                key={item.id}
                title={item.designation}
                description={item.description}
                date={item.date}
              />
            ))}
          </Route>
        </Switch>
      </div>
    </div>
  );
};

export default Category;
