import React from 'react';
import './style.css';

const EmployeeTable = ({ employees, showActiveOnly }) => {
  // Get the fields from the first employee object
  const filteredEmployees = showActiveOnly
    ? employees.filter(employee => employee.status === 'Active')
    : employees.filter(employee => employee.status === 'Inactive');

  const fields = filteredEmployees.length > 0 ? Object.keys(filteredEmployees[0]) : [];

  return (
    <table className="table">
      <thead>
        <tr>
          {fields.map((field) => (
            <th key={field}>{field}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {filteredEmployees.map((employee) => (
          <tr key={employee.id}>
            {fields.map((field) => (
              <td key={field}>{employee[field]}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default EmployeeTable;
