import React, { useState, useEffect } from 'react';
import { Switch, Route, Link } from 'react-router-dom';
import axios from 'axios';

import Home from './Home';
import Category from './Category';
import Header from './Header';
import Sidebar from './Sidebar';

import sampleData from './sampleData.json';

const App = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    //fetchEmployeesData();
    setEmployees(sampleData.employeeData);
  }, []);

  const fetchEmployeesData = async () => {
    try {
      //const response = await axios.get('https://testsyncoffice.netlify.app/.netlify/functions/api/getEmployeesData');
      const response = await axios.get('netlify/functions/api/getEmployeesData');
      setEmployees(response.data.employeeData);
    } catch (error) {
      console.error('Error fetching employees data:', error);
    }
  };

  return (
    <div>
      <Header />
      <div className="container">
        <Sidebar />
        <main>
          <Switch>
            <Route exact path="/" render={() => <Home employees={employees} />} />
            <Route path="/category" component={Category} />
          </Switch>
        </main>
      </div>
    </div>
  );
};

export default App;
