import React from 'react';
import { NavLink } from 'react-router-dom';
import './Tab.css';

const Tab = ({ onTabChange, selectedTab }) => {
  const handleTabClick = (tab) => {
    if (onTabChange) {
      onTabChange(tab);
    }
  };

  return (
    <nav className="tab-container">
      <NavLink
        to="/category/department"
        activeClassName="active"
        className={`tab-link ${selectedTab === 'department' ? 'active' : ''}`}
        onClick={() => handleTabClick('department')}
      >
        Department
      </NavLink>
      <NavLink
        to="/category/designation"
        activeClassName="active"
        className={`tab-link ${selectedTab === 'designation' ? 'active' : ''}`}
        onClick={() => handleTabClick('designation')}
      >
        Designation
      </NavLink>
    </nav>
  );
};

export default Tab;
