import React, { useState, useEffect } from 'react';
import axios from 'axios';

import Tab from './Tab';
import Card from './Card';
import sampleCardData from './sampleCardData.json';

const Category = () => {
  const [activeTab, setActiveTab] = useState('Department');
  const [departmentData, setDepartmentData] = useState([]);
  const [designationData, setDesignationData] = useState([]);

  useEffect(() => {
    fetchDepartmentData();
    fetchDesignationData();
  }, []);

  const fetchDepartmentData = async () => {
    try {
      const response = await axios.get('https://testsyncoffice.netlify.app/.netlify/functions/api/getDepartmentData');
      setDepartmentData(response.data);
      setDepartmentData(sampleCardData.designationData);
    } catch (error) {
      console.error('Error fetching department data:', error);
    }
  };

  const fetchDesignationData = async () => {
    try {
      // const response = await axios.get('https://testsyncoffice.netlify.app/.netlify/functions/api/getDesignationData');
      // setDesignationData(response.data);
      setDesignationData(sampleCardData.designationData);
    } catch (error) {
      console.error('Error fetching designation data:', error);
    }
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const renderTabContent = () => {
    if (activeTab === 'Department') {
      return (
        <div>
          <h2>Department Data</h2>
          {departmentData.map((department) => (
            <Card key={department.id} title={department.title} description={department.description} date={department.date} />
          ))}
        </div>
      );
    } else if (activeTab === 'Designation') {
      return (
        <div>
          <h2>Designation Data</h2>
          {designationData.map((designation) => (
            <Card key={designation.id} title={designation.title} description={designation.description} date={designation.date} />
          ))}
        </div>
      );
    }

    return null;
  };

  return (
    <div>
      <h1>Category Page</h1>
      <nav>
        <ul className="nav-tabs">
          <Tab activeTab={activeTab} tabName="Department" onClick={handleTabChange} />
          <Tab activeTab={activeTab} tabName="Designation" onClick={handleTabChange} />
        </ul>
      </nav>
      <div className="tab-content">{renderTabContent()}</div>
    </div>
  );
};

export default Category;
