import React, { useState, useEffect } from 'react';
import { Switch, Route, Link, Redirect } from 'react-router-dom';
import axios from 'axios';

import './style.css'; // Import the CSS file

import Home from './Home';
import Category from './Category';
import Header from './Header';
import Sidebar from './Sidebar';

const App = () => {
  const [employees, setEmployees] = useState([]);
  const [activeOnly, setActiveOnly] = useState(false);

  useEffect(() => {
    fetchEmployeesData();
  }, []);

  const fetchEmployeesData = async () => {
    try {
      //const response = await axios.get('https://testsyncoffice.netlify.app/.netlify/functions/api/getEmployeesData');
      const response = await axios.get('/api/.netlify/functions/api/getEmployeesData');

      setEmployees(response.data.employeeData);
    } catch (error) {
      console.error('Error fetching employees data:', error);
    }
  };

  const toggleActiveOnly = () => {
    setActiveOnly(!activeOnly);
  };

  return (
    <div>
      <Header />
      <div className="container">
        <Sidebar />
        <main className="content">
          <Switch>
            <Route exact path="/home">
              <Home employees={employees} activeOnly={activeOnly} toggleActiveOnly={toggleActiveOnly} />
            </Route>
            <Route path="/category" component={Category} />
            <Redirect to="/home" />
          </Switch>
        </main>
      </div>
    </div>
  );
};

export default App;
