import React, { useState } from 'react';

const Home = ({ employees }) => {
  const [activeOnly, setActiveOnly] = useState(false);

  const toggleActiveOnly = () => {
    setActiveOnly(!activeOnly);
  };

  const filteredEmployees = activeOnly ? employees.filter(employee => employee.status === 'Active') : employees;

  const tableHeaders = Object.keys(filteredEmployees[0] || {});

  return (
    <div>
      <h2>Home</h2>
      <div className="form-check form-switch">
        <input className="form-check-input" type="checkbox" id="activeOnlySwitch" checked={activeOnly} onChange={toggleActiveOnly} />
        <label className="form-check-label" htmlFor="activeOnlySwitch">Show active employees only</label>
      </div>
      <table className="table table-striped">
        <thead>
          <tr>
            {tableHeaders.map(header => (
              <th key={header}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredEmployees.map(employee => (
            <tr key={employee.id}>
              {tableHeaders.map(header => (
                <td key={header}>{employee[header]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
