import React, { useState } from 'react';
import Switch from 'react-switch';
import './style.css';

const Home = ({ employees }) => {
  const [activeOnly, setActiveOnly] = useState(false);

  // Extracting the headers dynamically from the first employee object
  const headers = employees.length > 0 ? Object.keys(employees[0]) : [];

  const handleToggle = () => {
    setActiveOnly(!activeOnly);
  };

  // Filtering the employees based on the toggle switch state
  const filteredEmployees = employees.filter(
    (employee) => employee.status === (activeOnly ? 'Active' : 'Inactive')
  );

  return (
    <div>
      <h2>Employees</h2>

      {/* <label>
