import React, { useState, useEffect } from 'react';
import ToggleSwitch from './ToggleSwitch';
import EmployeeTable from './EmployeeTable';
import './style.css';
import sampleData from './sampleData.json';


const Home = () => {
  const [employees, setEmployees] = useState([]);
  const [isActiveOnly, setIsActiveOnly] = useState(false);

  useEffect(() => {
    fetchEmployeesData();
  }, []);

  const fetchEmployeesData = async () => {
    try {
      // Your API call to fetch employee data
      // const response = await fetch('https://api.example.com/employees');
      // const data = await response.json();
      //setEmployees(data);
      setEmployees(sampleData.employeeData);
    } catch (error) {
      console.error('Error fetching employees data:', error);
    }
  };

  const handleToggleSwitch = (checked) => {
    setIsActiveOnly(checked);
  };

  const filteredEmployees = employees.filter((employee) => {
    if (statusFilter === 'All') {
      return true;
    }
    return employee.status === statusFilter;
  });

  return (
    <div className="container">
      <h2>Employee Directory</h2>
      <div className="status-filter">
        <span>Status:</span>
        <ToggleSwitch isActive={statusFilter === 'Active'} onToggle={handleStatusToggle} />
      </div>
      <EmployeeTable employees={filteredEmployees} />
    </div>
  );
};

export default Home;
