import React from 'react';
import { Link } from 'react-router-dom';

const NavigationTab = ({ selectedTab, onTabChange }) => {
  return (
    <div className="navigation-tab">
      <button
        className={`tab-button ${selectedTab === 'department' ? 'active' : ''}`}
        onClick={() => onTabChange('department')}
      >
        Department
      </button>
      <button
        className={`tab-button ${selectedTab === 'designation' ? 'active' : ''}`}
        onClick={() => onTabChange('designation')}
      >
        Designation
      </button>
    </div>
  );
};

export default NavigationTab;
