import React, { useState } from 'react';

const Home = ({ employees }) => {
  const [isActive, setIsActive] = useState(true); // Toggle switch state

  // Extracting the headers dynamically from the first employee object
  const headers = employees.length > 0 ? Object.keys(employees[0]) : [];

  // Filtering the employees based on the toggle switch state
  const filteredEmployees = employees.filter(
    (employee) => employee.status === (isActive ? 'Active' : 'Inactive')
  );

  return (
    <div>
      <h2>Home Page</h2>

      {/* Toggle switch */}
      <div className="form-check form-switch">
        <input
          className="form-check-input"
          type="checkbox"
          id="toggleSwitch"
          checked={isActive}
          onChange={() => setIsActive(!isActive)}
        />
        <label className="form-check-label" htmlFor="toggleSwitch">
          {isActive ? 'Active' : 'Inactive'}
        </label>
      </div>

      {/* Table */}
      <table className="table">
        <thead>
          <tr>
            {headers.map((header) => (
              <th key={header}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredEmployees.map((employee) => (
            <tr key={employee.id}>
              {headers.map((header) => (
                <td key={header}>{employee[header]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
