import React, { useState } from 'react';
import Switch from 'react-switch';
import './style.css';

const Home = ({ employees }) => {
  const [statusFilter, setStatusFilter] = useState('All');

  // Extracting the headers dynamically from the first employee object
  const headers = employees.length > 0 ? Object.keys(employees[0]) : [];

  const handleStatusToggle = (status) => {
    setStatusFilter(status);
  };

  // Filtering the employees based on the toggle switch state
  const filteredEmployees = employees.filter(
    (employee) => employee.status === (activeOnly ? 'Active' : 'Inactive')
  );

  return (
    <div>
      <h2>Employees</h2>

      {/* <label>
        Show Active Only:
        <Switch checked={activeOnly} onChange={handleToggle} />
      </label> */}
      <div className="status-filter">
        <span>Status:</span>
        <ToggleSwitch isActive={statusFilter === 'Active'} onToggle={handleStatusToggle} />
      </div>

      {/* Table */}
      <table className="table">
        <thead>
          <tr>
            {headers.map((header) => (
              <th key={header}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredEmployees.map((employee) => (
            <tr key={employee.id}>
              {headers.map((header) => (
                <td key={header}>{employee[header]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
